import React from "react";
// import katieZaferes from './images/katie-zaferes.png'
import star from "./images/star.png";

export default function Card(props) {
  let badgeText;
  if (props.openSpots === 0) {
    badgeText = "SOLD OUT";
  } else if (props.location === "Online") {
    badgeText = "ONLINE";
  }

  return (
    <div className="card">
      {badgeText && <div className="badge">{badgeText}</div>}
      <img
        className="card--image"
        src={require(`./images/${props.coverImg}`)}
        alt="airbnb pic"
      />
      <div>
        <img className="card--star" src={star} alt="star pic" />
        <span>{props.stats.rating}</span>
        <span className="gray">({props.stats.reviewCount}) â€¢ </span>
        <span className="gray">{props.location}</span>
        <p className="descipt" style={{textOverflow: 'clip'}}>{props.description.length > 70
                        ? props.description.substring(0, 67) + '...'
                        : props.description}</p>
      </div>
      <p className="life-text" style={{textOverflow: 'clip'}}>{props.title.length > 25 ? props.title.substring(0,24) + '...' : props.title}</p>
      <p className="price-text">
        <span className="bold">From ${props.price}</span> / person
      </p>
    </div>
  );
}
