import React from 'react'
// import katieZaferes from './images/katie-zaferes.png'
import star from "./images/star.png"

export default function Card(props) {
   let badgeText
   if(props.openSpots === 0) {
        badgeText = "SOLD OUT"
   } else if (props.location === "Online") {
        badgeText = "ONLINE"
   }   

    return (
        <div className="card">
            {badgeText && <div className="badge">{badgeText}</div>}
            {/* <img className="card--image" src={require(`./images/${props.element.img}`)} alt="airbnb pic"/> */}
            <div>
                <img className="card--star" src={star} alt="star pic"/>
                <span>{props.element.stats.rating}</span>
                <span className="gray">({props.element.stats.reviewCount}) â€¢ </span>
                <span className="gray">{props.element.country}</span>
            </div>
            <p className="life-text">{props.element.title}</p>
            <p className="price-text"><span className="bold">From ${props.element.price}</span> / person</p>
        </div>
    )
}
